import React, { useState } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

const CategoryCard = ({ name, image, link, index }) => {
  const controls = useAnimation();
  const [isHovered, setIsHovered] = useState(false);
const navigate=useNavigate()
  React.useEffect(() => {
    controls.start({
      y: [0, -10, 0],
      transition: {
        duration: 3,
        repeat: Infinity,
        repeatType: 'reverse',
        ease: 'easeInOut',
        delay: index * 0.2,
      },
    });
  }, [controls, index]);

  return (
    <div onClick={()=>{
      navigate(`/shop?${link}`)
    }}>
    <motion.div
      className="relative overflow-hidden  shadow-lg rounded-lg h-44 "
      animate={controls}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
      whileHover={{ scale: 1.05, zIndex: 1 }}
    >
      <div className="aspect-w-1 aspect-h-1 ">
        <motion.img
          src={image}
          alt={name}
          className="object-cover w-full h-44"
          animate={{ scale: isHovered ? 1.1 : 1 }}
          transition={{ duration: 0.3 }}
        />
      </div>
      <motion.div
  className="absolute inset-0 bg-gradient-to-t from-black via-transparent to-transparent flex items-end p-4"
  initial={{ opacity: 0.6 }}
  animate={{ opacity: isHovered ? 0.9 : 0.6 }} // Bolder opacity on hover
>
  <motion.h3
    className="text-gray-100 text-xl font-bold w-full text-center"
    initial={{ y: 40, opacity: 0 }}
    animate={{ y: isHovered ? 0 : 10, opacity: isHovered ? 1 : 0.8 }} // Smooth hover transition with fade-in
    transition={{ duration: 0.3, ease: "easeInOut" }} // Smoother ease transition
    style={{
      fontFamily: "'Poppins', sans-serif", // Sleek and modern font
      fontSize: '1rem', // Adjusted for balance
      textShadow: "1px 1px 3px rgba(0, 0, 0, 0.5)", // Subtle shadow for depth
      letterSpacing: '0.05em', // Slight spacing for clarity
    }}
  >
    {name}
  </motion.h3>
</motion.div>

    </motion.div>
    </div>
  );
};

const ProductCategories = () => {
  const categories = [
    { id: 1, name: "Makeup", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSpYIrxT3U5NRpkbFD7i4B2vdfWV17DXRQRow&s", link: "makeup" },
    { id: 2, name: "Skincare", image: "data:image/jpeg;base64,", link: "skincare" },
    { id: 3, name: "Haircare", image: "data:image/jpeg;base64,", link: "haircare" },
    { id: 4, name: "Fragrance", image: "data:image/jpeg;base64,", link: "fragrance" },
    { id: 5, name: "Nail Products", image: "https://img.freepik.com/free-photo/nail-care-products-arrangement-top-view_23-2149046500.jpg?size=626&ext=jpg&ga=GA1.2.1136005002.1726668034&semt=ais_hybrid", link: "nail" },
    { id: 6, name: "Body Care", image: "https://img.freepik.com/free-photo/women-s-cosmetic-products-placed-blue_1150-17127.jpg?ga=GA1.2.1136005002.1726668034&semt=ais_hybrid", link: "bodycare" },
  ];

  return (
    <section className="py-16 ">
      <div className="container mx-auto px-4">
        <motion.h2
          className="text-4xl font-bold text-center mb-8 text-gray-800"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.2 }}
        >
          Explore Our Collections
        </motion.h2>
        <motion.p
          className="text-gray-600 text-center mb-12 max-w-2xl mx-auto"
          initial={{ opacity: 0, y: -30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.4 }}
        >
          Discover premium beauty products curated to elevate your self-care routine and enhance your natural radiance.
        </motion.p>
        <motion.div
          className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-6"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 0.6 }}
        >
          {categories.map((category, index) => (
            <CategoryCard key={category.id} {...category} index={index} />
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default ProductCategories;
